'use strict';

// MODULES //

var isObject = require( '@stdlib/utils/is-object' );
var isString = require( '@stdlib/utils/is-string' );


// IS NODE //

/**
* Returns a boolean indicating if the runtime is Node.js.
*
* @returns {boolean} boolean indicating if runtime is Node.js
*
* @example
* var bool = isNode;
* // returns <boolean>
*/
function isNode() {
	if ( isObject( process ) && isObject( process.versions ) && isString( process.versions.node ) ) {
		return true;
	}
	return false;
} // end FUNCTION isNode()


// EXPORTS //

module.exports = isNode;
