'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isNode = require( './../lib/is_node.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNode, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if runtime is Node.js', function test( t ) {
	var isNode;

	function nativeClass( val ) {
		if ( val === process ) {
			return '[object process]';
		}
		return '[object global]';
	}

	function isString(){}
	isString.isPrimitive = alwaysTrue;

	function alwaysTrue() {
		return true;
	}

	isNode = proxyquire( './../lib/is_node.js', {
		'@stdlib/utils/native-class': nativeClass,
		'@stdlib/utils/is-plain-object': alwaysTrue,
		'@stdlib/utils/is-string': isString,
		'./global_scope.js': true
	});

	t.equal( isNode(), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if runtime is not Node.js (`global` variable does not equal the detected global variable)', function test( t ) {
	var isNode;

	function getGlobal() {
		return {};
	}

	function nativeClass( val ) {
		if ( val === process ) {
			return '[object process]';
		}
		return '[object global]';
	}

	function isString(){}
	isString.isPrimitive = alwaysTrue;

	function alwaysTrue() {
		return true;
	}

	isNode = proxyquire( './../lib/is_node.js', {
		'system.global': getGlobal,
		'@stdlib/utils/native-class': nativeClass,
		'@stdlib/utils/is-plain-object': alwaysTrue,
		'@stdlib/utils/is-string': isString,
		'./global_scope.js': true
	});
	t.equal( isNode(), false, 'returns false' );
	t.end();
});

