'use strict';

/**
* Tests if a value is a nonnegative integer.
*
* @module @stdlib/utils/is-nonnegative-integer
*
* @example
* var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' );
*
* var bool = isNonNegativeInteger( 5.0 );
* // returns true
*
* bool = isNonNegativeInteger( new Number( 5.0 ) );
* // returns true
*
* bool = isNonNegativeInteger( -5.0 );
* // returns false
*
* bool = isNonNegativeInteger( 3.14 );
* // returns false
*
* bool = isNonNegativeInteger( null );
* // returns false
* @example
* // Use interface to check for non-negative integer primitives...
* var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isPrimitive;
*
* var bool = isNonNegativeInteger( 3.0 );
* // returns true
*
* bool = isNonNegativeInteger( new Number( 3.0 ) );
* // returns false
* @example
* // Use interface to check for non-negative integer objects...
* var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isObject;
*
* var bool = isNonNegativeInteger( 3.0 );
* // returns false
*
* bool = isNonNegativeInteger( new Number( 3.0 ) );
* // returns true
* @example
* // Use interface to check for non-negative integer arrays...
* var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isNonNegativeIntegerArray;
*
* var bool = isNonNegativeInteger( [ 3.0, new Number(3.0) ] );
* // returns true
*
* bool = isNonNegativeInteger( [ 3.0, '3.0' ] );
* // returns false
* @example
* // Use interface to check for primitive non-negative integer arrays...
* var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isNonNegativePrimitiveIntegerArray;
*
* var bool = isNonNegativeInteger( [ 1.0, 0.0, 10.0 ] );
* // returns true
*
* bool = isNonNegativeInteger( [ 3.0, new Number(1.0) ] );
* // returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-like-function' );
var isNonNegativeInteger = require( './generic.js' );
var isPrimitive = require( './primitive.js' );
var isObject = require( './object.js' );


// METHODS //

setReadOnly( isNonNegativeInteger, 'isPrimitive', isPrimitive );
setReadOnly( isNonNegativeInteger, 'isObject', isObject );
setReadOnly( isNonNegativeInteger, 'isNonNegativeIntegerArray', arrayfun.create( isNonNegativeInteger ) );
setReadOnly( isNonNegativeInteger, 'isNonNegativePrimitiveIntegerArray', arrayfun.create( isPrimitive ) );


// EXPORTS //

module.exports = isNonNegativeInteger;
