'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonNegativeInteger = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonNegativeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a non-negative integer', function test( t ) {
	t.ok( isNonNegativeInteger( 5 ), 'returns true' );
	t.ok( isNonNegativeInteger( 0 ), 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a non-negative integer', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		Number.POSITIVE_INFINITY,
		Number.NEGATIVE_INFINITY,
		Math.PI,
		null,
		undefined,
		true,
		NaN,
		function(){},
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isNonNegativeInteger( values[i] ), false, 'returns false' );
	}
	t.end();
});
