'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonNegativeNumberArray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonNegativeNumberArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function tests for an array-like object containing only nonnegative numbers', function test( t ) {
	var arr;

	arr = [ 5.0, new Number( 5 ), 0 ];
	t.equal( isNonNegativeNumberArray( arr ), true, 'returns true' );

	arr = [ 5.0, '3', null ];
	t.equal( isNonNegativeNumberArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for an array-like object having only nonnegative number primitives', function test( t ) {
	var arr;

	arr = [ 5.0, 0.0 ];
	t.equal( isNonNegativeNumberArray.primitives( arr ), true, 'returns true' );

	arr = [ new Number( 5 ), 1.0, 1.0 ];
	t.equal( isNonNegativeNumberArray.primitives( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for an array-like object having only nonnegative number objects', function test( t ) {
	var arr;

	arr = [ new Number( 5 ), new Number( 5 ) ];
	t.equal( isNonNegativeNumberArray.objects( arr ), true, 'returns true' );

	arr = [ 5.0, 0.0 ];
	t.equal( isNonNegativeNumberArray.objects( arr ), false, 'returns false' );

	t.end();
});
