'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonNegativeNumber = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonNegativeNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a nonnegative number value', function test( t ) {
	t.equal( typeof isNonNegativeNumber.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a nonnegative number value', function test( t ) {
	t.equal( typeof isNonNegativeNumber.isObject, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a generic number array containing only nonnegative number values', function test( t ) {
	t.equal( typeof isNonNegativeNumber.isNonNegativeNumberArray, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number array containing only nonnegative number values', function test( t ) {
	t.equal( typeof isNonNegativeNumber.isNonNegativePrimitiveNumberArray, 'function', 'export is a function' );
	t.end();
});
