'use strict';

/**
* Test if a value is an array-like object containing only nonpositive integers.
*
* @module @stdlib/utils/is-nonpositive-integer-array
*
* @example
* var isNonPositiveIntegerArray = require( '@stdlib/utils/is-nonpositive-integer-array' );
*
* var bool = isNonPositiveIntegerArray( [ -3.0, new Number(-3.0) ] );
* // returns true
*
* bool = isNonPositiveIntegerArray( [ -3.0, '-3.0' ] );
* // returns false
*
* @example
* // Use interface to check for primitives...
* var isNonPositiveIntegerArray = require( '@stdlib/utils/is-nonpositive-integer-array' ).primitives;
*
* var bool = isNonPositiveIntegerArray( [ -1.0, 0.0, -10.0 ] );
* // returns true
*
* bool = isNonPositiveIntegerArray( [ -3.0, new Number(-1.0) ] );
* // returns false
*
* @example
* // Use interface to check for objects...
* var isNonPositiveIntegerArray = require( '@stdlib/utils/is-nonpositive-integer-array' ).objects;
*
* var bool = isNonPositiveIntegerArray( [ new Number(-3.0), new Number(-1.0) ] );
* // returns true
*
* bool = isNonPositiveIntegerArray( [ -1.0, 0.0, -10.0 ] );
* // returns false
*/

// MODULES //

var isNonPositiveInteger = require( '@stdlib/utils/is-nonpositive-integer' );
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-like-function' );


// MAIN //

var isNonPositiveIntegerArray = arrayfun.create( isNonPositiveInteger );
setReadOnly( isNonPositiveIntegerArray, 'primitives', arrayfun.create( isNonPositiveInteger.isPrimitive ) );
setReadOnly( isNonPositiveIntegerArray, 'objects', arrayfun.create( isNonPositiveInteger.isObject ) );


// EXPORTS //

module.exports = isNonPositiveIntegerArray;
