'use strict';

/**
* Tests if a value is a nonpositive integer.
*
* @module @stdlib/utils/is-nonpositive-integer
*
* @example
* var isNonPositiveInteger = require( '@stdlib/utils/is-nonpositive-integer' );
*
* var bool = isNonPositiveInteger( -5.0 );
* // returns true
*
* bool = isNonPositiveInteger( new Number( -5.0 ) );
* // returns true
*
* bool = isNonPositiveInteger( 5.0 );
* // returns false
*
* bool = isNonPositiveInteger( -3.14 );
* // returns false
*
* bool = isNonPositiveInteger( null );
* // returns false
* @example
* // Use interface to check for nonpositive integer primitives...
* var isNonPositiveInteger = require( '@stdlib/utils/is-nonpositive-integer' ).isPrimitive;
*
* var bool = isNonPositiveInteger( -3.0 );
* // returns true
*
* bool = isNonPositiveInteger( new Number( -3.0 ) );
* // returns false
* @example
* // Use interface to check for nonpositive integer objects...
* var isNonPositiveInteger = require( '@stdlib/utils/is-nonpositive-integer' ).isObject;
*
* var bool = isNonPositiveInteger( -3.0 );
* // returns false
*
* bool = isNonPositiveInteger( new Number( -3.0 ) );
* // returns true
* @example
* // Use interface to check for nonpositive integer arrays...
* var isNonPositiveInteger = require( '@stdlib/utils/is-nonpositive-integer' ).isNonPositiveIntegerArray;
*
* var bool = isNonNegativeInteger( [ -3.0, new Number(-3.0) ] );
* // returns true
*
* bool = isNonNegativeInteger( [ -3.0, '-3.0' ] );
* // returns false
* @example
* // Use interface to check for primitive nonpositive integer arrays...
* var isNonPositiveInteger = require( '@stdlib/utils/is-nonpositive-integer' ).isNonPositivePrimitiveIntegerArray;
*
* var bool = isNonNegativeInteger( [ -1.0, 0.0, -10.0 ] );
* // returns true
*
* bool = isNonNegativeInteger( [ -3.0, new Number(-1.0) ] );
* // returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-like-function' );
var isNonPositiveInteger = require( './generic.js' );
var isPrimitive = require( './primitive.js' );
var isObject = require( './object.js' );


// METHODS //

setReadOnly( isNonPositiveInteger, 'isPrimitive', isPrimitive );
setReadOnly( isNonPositiveInteger, 'isObject', isObject );
setReadOnly( isNonPositiveInteger, 'isNonPositiveIntegerArray', arrayfun.create( isNonPositiveInteger ) );
setReadOnly( isNonPositiveInteger, 'isNonPositivePrimitiveIntegerArray', arrayfun.create( isPrimitive ) );


// EXPORTS //

module.exports = isNonPositiveInteger;
