'use strict';

/**
* Test if a value is an array-like object containing only nonpositive numbers.
*
* @module @stdlib/utils/is-nonpositive-number-array
*
* @example
* var isNonPositiveNumberArray = require( '@stdlib/utils/is-nonpositive-number-array' );
*
* var bool = isNonPositiveNumberArray( [ -3.0, new Number(-3.0) ] );
* // returns true
*
* bool = isNonPositiveNumberArray( [ -3.0, '-3.0' ] );
* // returns false
*
* @example
* // Use interface to check for primitives...
* var isNonPositiveNumberArray = require( '@stdlib/utils/is-nonpositive-number-array' ).primitives;
*
* var bool = isNonPositiveNumberArray( [ -1.0, 0.0, -10.0 ] );
* // returns true
*
* bool = isNonPositiveNumberArray( [ -3.0, new Number(-1.0) ] );
* // returns false
*
* @example
* // Use interface to check for objects...
* var isNonPositiveNumberArray = require( '@stdlib/utils/is-nonpositive-number-array' ).objects;
*
* var bool = isNonPositiveNumberArray( [ new Number(-3.0), new Number(-1.0) ] );
* // returns true
*
* bool = isNonPositiveNumberArray( [ -1.0, 0.0, -10.0 ] );
* // returns false
*/

// MODULES //

var isNonPositiveNumber = require( '@stdlib/utils/is-nonpositive-number' );
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-like-function' );


// VARIABLES //

var isNonPositiveNumberArray = arrayfun.create( isNonPositiveNumber );


// MAIN //

setReadOnly( isNonPositiveNumberArray, 'primitives', arrayfun.create( isNonPositiveNumber.isPrimitive ) );
setReadOnly( isNonPositiveNumberArray, 'objects', arrayfun.create( isNonPositiveNumber.isObject ) );


// EXPORTS //

module.exports = isNonPositiveNumberArray;
