'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonPositiveNumber = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonPositiveNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a nonpositive number value', function test( t ) {
	t.equal( typeof isNonPositiveNumber.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a nonpositive number value', function test( t ) {
	t.equal( typeof isNonPositiveNumber.isObject, 'function', 'export is a function' );
	t.end();
});
