'use strict';

var isNullArray = require( './../lib' );

console.log( isNullArray( null ) );
// => false

console.log( isNullArray( 'beep' ) );
// => false

console.log( isNullArray( [ null, 'boop' ] ) );
// => false

console.log( isNullArray( [ null ] ) );
// => true

console.log( isNullArray( [] ) );
// => false

console.log( isNullArray( [ null, null, null ] ) );
// => true
