'use strict';

/**
* Test if a value is an array having only null values.
*
* @module @stdlib/utils/is-null-array
*
* @example
* var isNullArray = require( '@stdlib/utils/is-null-array' );
*
* var bool = isNullArray( [null,null,null] );
* // returns true
*
* bool = isNullArray( [NaN,2,null] );
* // returns false
*/

// MODULES //

var arrayfun = require( '@stdlib/utils/tools/array-like-function' );
var isNull = require( '@stdlib/utils/is-null' );


// VARIABLES //

var isNullArray = arrayfun.create( isNull );


// EXPORTS //

module.exports = isNullArray;
