'use strict';

var isNull = require( './../lib' );

console.log( isNull( null ) );
// => true

console.log( isNull( 'beep' ) );
// => false

console.log( isNull( 5 ) );
// => false

console.log( isNull( undefined ) );
// => false

console.log( isNull( true ) );
// => false

console.log( isNull( {} ) );
// => false

console.log( isNull( [] ) );
// => false

console.log( isNull( function foo(){} ) );
// => false
