/* eslint-disable no-new-wrappers */
'use strict';

var isNumberArray = require( './../lib' );

var bool = isNumberArray( [ 3.14 ] );
console.log( bool );
// => true

bool = isNumberArray( [ NaN ] );
console.log( bool );
// => true

bool = isNumberArray( [ 1, 2, 3 ] );
console.log( bool );
// => true

bool = isNumberArray( [ new Number( 3.14 ), 2, 3 ] );
console.log( bool );
// => true

bool = isNumberArray( 3.14 );
console.log( bool );
// => false

bool = isNumberArray( [] );
console.log( bool );
// => false

bool = isNumberArray( [ '1', 2 ] );
console.log( bool );
// => false
