'use strict';

// FUNCTIONS //

var toString = Object.prototype.toString;
var validate = require( '@stdlib/tools/array-like-function' );


// IS NUMBER //

/**
* FUNCTION: isNumber( value )
*	Tests if a value is a number.
*
* @param {*} value - value to test
* @returns {Boolean} boolean indicating whether value is a number
*/
function isNumber( value ) {
	return ( typeof value === 'number' || toString.call( value ) === '[object Number]' ) && value.valueOf() === value.valueOf();
} // end FUNCTION isNumber()


// EXPORTS //

module.exports = isNumber;

module.exports.primitive = require( '@stdlib/utils/is-number/lib/primitive.js' );

module.exports.array = validate.create( isNumber );
