'use strict';

// MODULES //

var tape = require( 'tape' );
var isNumber = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic number array', function test( t ) {
	var arr;

	arr = [ 5.0, new Number( 5 ), NaN, new Number( NaN ) ];
	t.equal( isNumber.isNumberArray( arr ), true, 'returns true' );

	arr = [ 5.0, '3', null ];
	t.equal( isNumber.isNumberArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive number array', function test( t ) {
	var arr;

	arr = [ 5, -3.15, NaN, -0.0 ];
	t.equal( isNumber.isPrimitiveNumberArray( arr ), true, 'returns true' );

	arr = [ new Number( 5 ), 1.0, 1.0 ];
	t.equal( isNumber.isPrimitiveNumberArray( arr ), false, 'returns false' );

	t.end();
});
