'use strict';

// MODULES //

var tape = require( 'tape' );
var isNumber = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number', function test( t ) {
	t.equal( typeof isNumber.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object', function test( t ) {
	t.equal( typeof isNumber.isObject, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a generic number array', function test( t ) {
	t.equal( typeof isNumber.isArray, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number array', function test( t ) {
	t.equal( typeof isNumber.isPrimitiveArray, 'function', 'export is a function' );
	t.end();
});
