'use strict';

// MODULES //

var tape = require( 'tape' );
var isNumber = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a number', function test( t ) {
	t.equal( isNumber( 5 ), true, 'returns true' );
	t.equal( isNumber(  new Number( 5 ) ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a number', function test( t ) {
	var values;
	var i;

	values = [
		true,
		[],
		'5',
		function(){},
		null,
		{},
		NaN,
		new Number( NaN ),
		undefined
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isNumber( values[i] ), false, 'returns false' );
	}
	t.end();
});
