'use strict';

// MODULES //

var tape = require( 'tape' );
var isNumber = require( './../lib/primitive.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a primitive number', function test( t ) {
	t.equal( isNumber( 3.14 ), true, 'returns true' );
	t.equal( isNumber( NaN ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a number object', function test( t ) {
	t.equal( isNumber( new Number( 5 ) ), false, 'returns false' );
	t.equal( isNumber( new Number( NaN ) ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		null,
		true,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isNumber( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
