'use strict';

var isNumericArray = require( './../lib' );

var arr = new Int8Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint8Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint8ClampedArray( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Int16Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint16Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Int32Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint32Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Float32Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Float64Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = [ 1, 2, 3 ];
console.log( isNumericArray( arr ) );
// => true

console.log( isNumericArray( [] ) );
// => false

console.log( isNumericArray( {} ) );
// => false

console.log( isNumericArray( null ) );
// => false

console.log( isNumericArray( new Buffer( 10 ) ) );
// => false
