'use strict';

/**
* Tests if a value is object-like.
*
* @module @stdlib/utils/is-object-like
*
* @example
* var isObjectLike = require( '@stdlib/utils/is-object-like' );
*
* var bool = isObjectLike( {} );
* // returns true
*
* bool = isObjectLike( [] );
* // returns true
*
* @example
* var isObjectLike = require( '@stdlib/utils/is-object' ).isObjectLikeArray;
*
* var bool = isObjectLike( [ {}, [] ] );
* // returns true
*
* bool = isObjectLike( [ {}, '3.0' ] );
* // returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isObjectLike = require( './is_object_like.js' );
var isObjectLikeArray = require( './is_object_like_array.js' );


// METHODS //

setReadOnly( isObjectLike, 'isObjectLikeArray',  isObjectLikeArray );


// EXPORTS //

module.exports = isObjectLike;
