'use strict';

// MODULES //

var isArray = require( '@stdlib/utils/is-array' );
var isObjectLike = require( './is_object_like.js' );


// IS OBJECT-LIKE ARRAY //

/**
* Tests if a value is an array of object-like elements.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value is an array of object-like elements.
*
* @example
* var bool = isObjectLikeArray( [ {}, [] ] );
* // returns true
* @example
* var bool = isObjectLikeArray( [ {}, '3.0' ] );
* // returns false
*/
function isObjectLikeArray( value ) {
	var len;
	var i;
	if ( !isArray( value ) ) {
		return false;
	}
	len = value.length;
	if ( !len ) {
		return false;
	}
	for ( i = 0; i < len; i++ ) {
		if ( !isObjectLike( value[i] ) ) {
			return false;
		}
	}
	return true;
} // end FUNCTION isObjectLikeArray()


// EXPORTS //

module.exports = isObjectLikeArray;
