'use strict';

/**
* Test if a value is an object.
*
* @module @stdlib/utils/is-object
*
* @example
* var isObject = require( '@stdlib/utils/is-object' );
*
* var bool = isObject( {} );
* // returns true
*
* @example
* var isObject = require( '@stdlib/utils/is-object' ).isObjectArray;
*
* var bool = isObject( [ {}, new Number(3.0) ] );
* // returns true
*
* bool = isObject( [ {}, { 'beep': 'boop' } ] );
* // returns true
*
* bool = isObject( [ {}, '3.0' ] );
* // returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-function' );
var isObject = require( './is_object.js' );


// METHODS //

setReadOnly( isObject, 'isObjectArray',  arrayfun.create( isObject ) );


// EXPORTS //

module.exports = isObject;
