'use strict';

// MODULES //

var isArray = require( '@stdlib/utils/is-array' );
var isObject = require( './is_object.js' );


// IS OBJECT ARRAY //

/**
* Tests if a value is an object array.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value is an object array
*
* @example
* var bool = isObjectArray( [ {}, new Number(3.0) ] );
* // returns true
* @example
* var bool = isObjectArray( [ {}, { 'beep': 'boop' } ] );
* // returns true
* @example
* var bool = isObjectArray( [ {}, '3.0' ] );
* // returns false
*/
function isObjectArray( value ) {
	var len;
	var i;
	if ( !isArray( value ) ) {
		return false;
	}
	len = value.length;
	if ( !len ) {
		return false;
	}
	for ( i = 0; i < len; i++ ) {
		if ( !isObject( value[i] ) ) {
			return false;
		}
	}
	return true;
} // end FUNCTION isObjectArray()


// EXPORTS //

module.exports = isObjectArray;
