'use strict';

// MODULES //

var tape = require( 'tape' );
var isObject = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isObject, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided an object', function test( t ) {
	t.equal( isObject( {} ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided an object', function test( t ) {
	var values;
	var i;
	values = [
		5,
		[],
		true,
		'5',
		null,
		NaN,
		function(){},
		undefined
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isObject( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
