'use strict';

var isOdd = require( './../lib' );

console.log( isOdd( 5.0 ) );
// returns true

console.log( isOdd( 0.0 ) );
// returns false

console.log( isOdd( new Number( 5 ) ) );
// returns true

console.log( isOdd( 4.0 ) );
// returns false

console.log( isOdd( NaN ) );
// returns false

console.log( isOdd( '5' ) );
// returns false

console.log( isOdd( null ) );
// returns false
