'use strict';

// MODULES //

var isInteger = require( '@stdlib/utils/is-integer' ).isObject;
var isEven = require( '@stdlib/utils/is-even' ).isPrimitive;


// MAIN //

/**
* Tests if a value is a number object that has an odd number value.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value is a number object that has an odd number value
*
* @example
* var bool = isOdd( 5.0 );
* // returns false
* @example
* var bool = isOdd( new Number( 5.0 ) );
* // returns true
*/
function isOdd( value ) {
	if ( !isInteger( value ) ) {
		return false;
	}
	// Check sign to prevent overflow...
	if ( value > 0.0 ) {
		return isEven( value-1.0 );
	}
	return isEven( value+1.0 );
} // end FUNCTION isOdd()


// EXPORTS //

module.exports = isOdd;
