'use strict';

// MODULES //

var tape = require( 'tape' );
var isOdd = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isOdd, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic number array containing only odd numbers', function test( t ) {
	var arr;

	arr = [ 5.0, new Number( 5 ), -5.0 ];
	t.equal( isOdd.isOddArray( arr ), true, 'returns true' );

	arr = [ 4.0, '3', null ];
	t.equal( isOdd.isOddArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive number array containing only odd numbers', function test( t ) {
	var arr;

	arr = [ 5.0, -1.0 ];
	t.equal( isOdd.isPrimitiveOddArray( arr ), true, 'returns true' );

	arr = [ new Number( 5 ), 1.0, 1.0 ];
	t.equal( isOdd.isPrimitiveOddArray( arr ), false, 'returns false' );

	t.end();
});
