'use strict';

// MODULES //

var tape = require( 'tape' );
var isOdd = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isOdd, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive odd number', function test( t ) {
	t.equal( typeof isOdd.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a value which is an odd number', function test( t ) {
	t.equal( typeof isOdd.isObject, 'function', 'export is a function' );
	t.end();
});
