'use strict';

/**
* Test if a value is an array-like object containing only plain objects.
*
* @module @stdlib/utils/is-plain-object-array
*
* @example
* var isObjectArray = require( '@stdlib/utils/is-plain-object-array' );
*
* var bool = isObjectArray( [ {}, { 'beep': 'boop' } ] );
* // returns true
*
* bool = isObjectArray( [ {}, new Number(3.0) ] );
* // returns false
*
* bool = isObjectArray( [ {}, '3.0' ] );
* // returns false
*/

// MODULES //

var arrayfun = require( '@stdlib/utils/tools/array-like-function' );
var isPlainObject = require( '@stdlib/utils/is-plain-object' );


// MAIN //

var isPlainObjectArray = arrayfun.create( isPlainObject );


// EXPORTS //

module.exports = isPlainObjectArray;
