'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonNegativeInteger = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonNegativeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a positive integer value', function test( t ) {
	t.equal( typeof isNonNegativeInteger.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a positive integer value', function test( t ) {
	t.equal( typeof isNonNegativeInteger.isObject, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a generic number array containing only positive integer values', function test( t ) {
	t.equal( typeof isNonNegativeInteger.isNonNegativeIntegerArray, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number array containing only positive integer values', function test( t ) {
	t.equal( typeof isNonNegativeInteger.isNonNegativePrimitiveIntegerArray, 'function', 'export is a function' );
	t.end();
});
