'use strict';

var isPositiveNumberArray = require( './../lib' );

var bool = isPositiveNumberArray( [ 5.0, 0.2, 3.9 ] );
console.log( bool );
// => true

bool = isPositiveNumberArray( [ 1, 2, 3 ] );
console.log( bool );
// => true

bool = isPositiveNumberArray( [ 1, new Number( 6 ), 3 ] );
console.log( bool );
// => true

bool = isPositiveNumberArray( [ 0, 2, 4 ] );
console.log( bool );
// => false

bool = isPositiveNumberArray( [ 1, 'abc', 3 ] );
console.log( bool );
// => false

bool = isPositiveNumberArray( 78.0 );
console.log( bool );
// => false

bool = isPositiveNumberArray( [] );
console.log( bool );
// => false
