'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveNumber = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isPositiveNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a positive value', function test( t ) {
	t.equal( typeof isPositiveNumber.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a positive value', function test( t ) {
	t.equal( typeof isPositiveNumber.isObject, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a generic number array containing only positive number values', function test( t ) {
	t.equal( typeof isPositiveNumber.isPositiveNumberArray, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number array containing only positive number values', function test( t ) {
	t.equal( typeof isPositiveNumber.isPositivePrimitiveNumberArray, 'function', 'export is a function' );
	t.end();
});
