'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveNumber = require( './../lib/primitive.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isPositiveNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a primitive number having a positive value', function test( t ) {
	t.equal( isPositiveNumber( 3.0 ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a number object, even if the number has a positive value', function test( t ) {
	t.equal( isPositiveNumber( new Number( 5.0 ) ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		null,
		true,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isPositiveNumber( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
