'use strict';

// MODULES //

var tape = require( 'tape' );
var isPrimitive = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isPrimitive, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for an array of primitives', function test( t ) {
	var arr;

	arr = [ 'abc', 123, null, NaN, undefined ];
	t.equal( isPrimitive.isPrimitiveArray( arr ), true, 'returns true' );

	arr = [ 5.0, '5.0', new Number( 2 ) ];
	t.equal( isPrimitive.isPrimitiveArray( arr ), false, 'returns false' );

	t.end();
});
