'use strict';

// MODULES //

var tape = require( 'tape' );
var isPrimitive = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isPrimitive, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a primitive', function test( t ) {
	var values = [
		'',
		0,
		false,
		undefined,
		null
	];

	for ( var i = 0; i < values.length; i++ ) {
		t.equal( isPrimitive( values[i] ), true, 'returns true' );
	}
	t.end();
});

tape( 'function returns `false` if not provided a primitive', function test( t ) {
	var values;
	var i;
	values = [
		new Boolean(),
		new String(),
		new Array(),
		new Object(),
		new Number(),
		function(){},
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isPrimitive( values[i] ), false, 'returns false for value: '+values[i] );
	}
	t.end();
});
