'use strict';

// MODULES //

var isNumericArray = require( '@stdlib/utils/is-numeric-array' );
var absdiff = require( '@stdlib/math/base/utils/absolute-difference' );
var FLOAT64_EPS = require( '@stdlib/math/constants/float64-eps' );


// MAIN //

/**
* Tests if a value is a probability array.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value is a probability array
*
* @example
* var bool = isProbabilityArray( [ 0.25, 0.5, 0.25 ] );
* // returns true
*
* bool = isProbabilityArray( Uint8Array( [ 0, 1 ] );
* // returns true
*
* bool = isProbabilityArray( [ 3.14, 0.0 ] );
* // returns false
*/
function isProbabilityArray( v ) {
	var sum;
	var i;
	if ( isNumericArray( v ) ) {
		sum = 0.0;
		for ( i = 0; i < v.length; i++ ) {
			if (
				v[ i ] > 1.0 ||
				v[ i ] < 0.0
			) {
				return false;
			}
			sum += v[ i ];
		}
		return ( absdiff( sum, 1.0 ) <= FLOAT64_EPS );
	}
	return false;
} // end FUNCTION isProbabilityArray()


// EXPORTS //

module.exports = isProbabilityArray;
