'use strict';

var isProbability = require( './../lib' );

console.log( isProbability( 0.5 ) );
// => true

console.log( isProbability( new Number( 0.5 ) ) );
// => true

console.log( isProbability( 0.0 ) );
// => true

console.log( isProbability( 1.0 ) );
// => true

console.log( isProbability( 3.14 ) );
// => false

console.log( isProbability( -5.0 ) );
// => false

console.log( isProbability( NaN ) );
// => false

console.log( isProbability( '0.5' ) );
// => false

console.log( isProbability( null ) );
// => false
