'use strict';

// MODULES //

var tape = require( 'tape' );
var isProbability = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isProbability, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive probability', function test( t ) {
	t.equal( typeof isProbability.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a probability value', function test( t ) {
	t.equal( typeof isProbability.isObject, 'function', 'export is a function' );
	t.end();
});
