'use strict';

/**
* Test if a provided prototype exists in an object's prototype chain.
*
* @module @stdlib/utils/is-prototype-of
*
* @example
* var inherit = require( '@stdlib/utils/inherit' );
* var isPrototypeOf = require( '@stdlib/utils/is-prototype-of' );
*
* function Foo() {
*     return this;
* }
*
* function Bar() {
*     return this;
* }
* inherit( Bar, Foo );
*
* var bar = new Bar();
*
* var bool = isPrototypeOf( bar, Foo.prototype );
* // returns true
*/

// MODULES //

var isPrototypeOf = require( './is_prototype_of.js' );


// EXPORTS //

module.exports = isPrototypeOf;
