'use strict';

var isRegExpString = require( './../lib' );

console.log( isRegExpString( '/beep/' ) );
// => true

console.log( isRegExpString( '/beep/gim' ) );
// => true

console.log( isRegExpString( 'beep' ) );
// => false

console.log( isRegExpString( '' ) );
// => false

console.log( isRegExpString( null ) );
// => false
