'use strict';

// MODULES //

var tape = require( 'tape' );
var isRegexp = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isRegexp, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a regular expression', function test( t ) {
	t.equal( isRegexp( /\.+/ ), true, 'returns true' );
	t.equal( isRegexp( new RegExp( '\.+' ) ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a regular expression', function test( t ) {
	var values;
	var i;

	values = [
		5,
		[],
		'5',
		'/\.+/',
		function(){},
		null,
		NaN,
		{},
		undefined
	];
	for ( i = 0; i < values.length; i++ ) {
		t.equal( isRegexp( values[i] ), false, 'returns false when provided '+values[i]  );
	}
	t.end();
});
