'use strict';

var isRelativePath = require( './../lib' );

var bool = isRelativePath.posix( 'foo/' );
console.log( bool );
// => true

bool = isRelativePath.posix( 'foo' );
console.log( bool );
// => true

bool = isRelativePath.posix( '.' );
console.log( bool );
// => true

bool = isRelativePath.posix( '' );
console.log( bool );
// => true

bool = isRelativePath.posix( '/foo/bar/baz' );
console.log( bool );
// => false

bool = isRelativePath.posix( '/foo/..' );
console.log( bool );
// => false

bool = isRelativePath.win32( 'foo\\bar\\baz' );
console.log( bool );
// => true

bool = isRelativePath.win32( 'foo/bar/baz' );
console.log( bool );
// => true

bool = isRelativePath.win32( 'foo/..' );
console.log( bool );
// => true

bool = isRelativePath.win32( '.' );
console.log( bool );
// => true

bool = isRelativePath.win32( '' );
console.log( bool );
// => true

bool = isRelativePath.win32( 'C:\\foo\\bar\\baz' );
console.log( bool );
// => false

bool = isRelativePath.win32( '//server' );
console.log( bool );
// => false

bool = isRelativePath.win32( '\\\\server' );
console.log( bool );
// => false

bool = isRelativePath.win32( 'C:/foo/bar/baz' );
console.log( bool );
// => false

bool = isRelativePath.win32( '/foo/..' );
console.log( bool );
// => false
