'use strict';

var isSafeInteger = require( './../lib' );

console.log( isSafeInteger( -5.0 ) );
// => true

console.log( isSafeInteger( 0.0 ) );
// => true

console.log( isSafeInteger( new Number( 5.0 ) ) );
// => true

console.log( isSafeInteger( 3.0e200 ) );
// => false

console.log( isSafeInteger( 5.256 ) );
// => false

console.log( isSafeInteger( 1.0/0.0 ) );
// => false

console.log( isSafeInteger( -1.0/0.0 ) );
// => false

console.log( isSafeInteger( NaN ) );
// => false

console.log( isSafeInteger( '5' ) );
// => false

console.log( isSafeInteger( null ) );
// => false
