/* eslint-disable no-new-wrappers, no-restricted-syntax, no-empty-function */
'use strict';

var isString = require( './../lib' );

console.log( isString( 'beep' ) );
// => true

console.log( isString( new String( 'beep' ) ) );
// => true

console.log( isString( 5 ) );
// => false

console.log( isString( null ) );
// => false

console.log( isString( void 0 ) );
// => false

console.log( isString( {} ) );
// => false

console.log( isString( [] ) );
// => false

console.log( isString( function foo() {} ) );
// => false
