'use strict';

// MODULES //

var typeOf = require( '@stdlib/utils/type-of' );
var validate = require( '@stdlib/tools/array-like-function' );


// IS STRING //

/**
* FUNCTION: isString( value )
*	Tests if a value is a string.
*
* @param {*} value - value to test
* @returns {Boolean} boolean indicating if a value is a string
*/
function isString( value ) {
	return ( typeOf( value ) === 'string' );
} // end FUNCTION isString()


// EXPORTS //

module.exports = isString;

module.exports.primitive = require( '@stdlib/utils/is-string/primitive.js' );

module.exports.array = validate.create( isString );
