'use strict';

// MODULES //

var valueOf = require( './valueof.js' );


// MAIN //

/**
* Attempts to extract a string value.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a string can be extracted
*/
function test( value ) {
	try {
		valueOf.call( value );
		return true;
	} catch ( err ) {
		return false;
	}
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
