'use strict';

// MODULES //

var tape = require( 'tape' );
var isString = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isString, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic string array', function test( t ) {
	var arr;

	arr = [ 'a', new String( 'b' ) ];
	t.equal( isString.isStringArray( arr ), true, 'returns true' );

	arr = [ 'a', 5, null ];
	t.equal( isString.isStringArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive string array', function test( t ) {
	var arr;

	arr = [ 'a', 'b' ];
	t.equal( isString.isPrimitiveStringArray( arr ), true, 'returns true' );

	arr = [ new String( 'a' ), 'b' ];
	t.equal( isString.isPrimitiveStringArray( arr ), false, 'returns false' );

	t.end();
});
