'use strict';

// MODULES //

var tape = require( 'tape' );
var isString = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isString, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a primitive string', function test( t ) {
	t.equal( isString( 'a' ), true, 'returns true' );
	t.end();
});

tape( 'function returns `true` if provided a String object', function test( t ) {
	t.equal( isString( new String('a') ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a string', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		null,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isString( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
