'use strict';

// MODULES //

var tape = require( 'tape' );
var isString = require( './../lib/primitive.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isString, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a primitive string', function test( t ) {
	t.equal( isString( 'a' ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a string object', function test( t ) {
	t.equal( isString( new String( 'a' ) ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a string', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		null,
		true,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isString( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
